package alkusi.kudmi.rabindranotes.adapter

import alkusi.kudmi.rabindranotes.R
import alkusi.kudmi.rabindranotes.databinding.ItemStaffBinding
import alkusi.kudmi.rabindranotes.model.StaffRes
import android.content.Context
import android.view.LayoutInflater
import android.view.ViewGroup
import android.widget.ListAdapter
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class MainAdapter(private val onItemClick:(StaffRes.MyData)->Unit,val context: Context):androidx.recyclerview.widget.ListAdapter<StaffRes.MyData,MainAdapter.ViewHolder>(MyDiffUtil())
{
    class MyDiffUtil():DiffUtil.ItemCallback<StaffRes.MyData>()
    {
        override fun areItemsTheSame(oldItem: StaffRes.MyData, newItem: StaffRes.MyData): Boolean {
          return oldItem==newItem
        }

        override fun areContentsTheSame(
            oldItem: StaffRes.MyData,
            newItem: StaffRes.MyData
        ): Boolean {
          return oldItem.equals(newItem)
        }

    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MainAdapter.ViewHolder {

    val binding = ItemStaffBinding.inflate(LayoutInflater.from(parent.context),parent,false)

    return ViewHolder(binding)
    }

    override fun onBindViewHolder(holder: MainAdapter.ViewHolder, position: Int) {
        val item = getItem(position)
        item?.let {
            holder.bindData(it)
        }
    }

    inner class ViewHolder(val binding: ItemStaffBinding):RecyclerView.ViewHolder(binding.root)
    {
        fun bindData(data:StaffRes.MyData)
        {
            val imageUrl = "data:image/jpeg;base64,"
            Glide.with(context).load(imageUrl).into(binding.imageView)
            binding.textView.setText(data.chapterName)
        }
    }
}
